import logo from './logo.svg';
import './App.css';


import React from "react";

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      state = {
        title: null,
        image: null,
        description: null
      };


      handleSubmit(e) {
        e.preventDefault();

        const formData = {};
        for (const field in this.refs) {
          formData[field] = this.refs[field].value;
        }
        console.log(formData.genre);



        const fetchAndLog = async () => {
            const response = await fetch('http://localhost:8000/api/genre=comedy');
            const json = await response.json();
            // just log ‘json’
            this.setState({ title: json.title, image:json.image, description:json.description});
        }

        fetchAndLog();


    //     fetch('http://localhost:8000/api/genre=comedy', {
    //     method: 'GET',
