import logo from './logo.svg';
import './App.css';


import React from "react";

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
      state = {
        person: null
      };

      async componentDidMount(genre) {
        const url = "http://localhost:8000/api/genre="+genre;
        const response = await fetch(url);
        const data = await response.json();
        this.setState({ person: data, loading: false });
      }

      handleSubmit(e) {
        e.preventDefault();

        const formData = {};
        for (const field in this.refs) {
          formData[field] = this.refs[field].value;
        }
        console.log(formData.genre);
        componentDidMount(formData.genre);
      }

  render() {

    return (
        <div>
        <form onSubmit={this.handleSubmit}>

          Select Movie Genre
          <select ref="genre" className="genre" name="genre">
              <option>Comedy</option>
              <option>Sci-fi</option>
              <option>Action</option>
          </select>
          <input type="submit" value="Submit"/>
        </form>
        <div>
        <div>  {this.state.person.title}</div>
        <img src={this.state.person.image} />
        <div>  {this.state.person.description}</div>
      </div>
      </div>

    );
  }
}
